import{_ as p,o as B,c as I,a as M}from"./app.ace0dae9.js";const _={},e=M('<h1 id="\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u4E0E-bim-\u6280\u672F\u5B9E\u73B0\u57CE\u5E02\u4E09\u7EF4\u6A21\u578B\u7684\u6784\u5EFA\u4E0E\u5E94\u7528" tabindex="-1"><a class="header-anchor" href="#\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u4E0E-bim-\u6280\u672F\u5B9E\u73B0\u57CE\u5E02\u4E09\u7EF4\u6A21\u578B\u7684\u6784\u5EFA\u4E0E\u5E94\u7528" aria-hidden="true">#</a> \u503E\u659C\u6444\u5F71\u6D4B\u91CF\u4E0E BIM \u6280\u672F\u5B9E\u73B0\u57CE\u5E02\u4E09\u7EF4\u6A21\u578B\u7684\u6784\u5EFA\u4E0E\u5E94\u7528</h1><p>\u968F\u7740\u7F51\u7EDC\u53CA\u8BA1\u7B97\u673A\u6280\u672F\u6C34\u5E73\u7684\u4E0D\u65AD\u63D0\u9AD8\uFF0C\u4EBA\u4EEC\u5F00\u59CB\u9010\u6E10\u8BA4\u8BC6\u5230\u667A\u6167\u57CE\u5E02\u6784\u5EFA\u5E94\u4EE5\u4E09\u7EF4\u6570\u5B57\u57CE\u5E02\u6A21\u578B\u6784\u5EFA\u4E3A\u5FC5\u8981\u6761\u4EF6\uFF0C\u800C\u5C06\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u548C BIM \u6280\u672F\u6709\u6548\u7ED3\u5408\u8D77\u6765\uFF0C\u5373\u901A\u8FC7\u65E0\u4EBA\u673A\u9065\u611F\u5F71\u50CF\u6784\u5EFA\u57CE\u5E02\u5730\u9762\u6A21\u578B\u53CA\u57CE\u5E02\u5355\u4F53\u5EFA\u7B51\u6784\u5EFA BIM \u6A21\u578B\u7B49\u65B9\u6CD5\u6574\u5408\u57CE\u5E02\u4E09\u7EF4\u57FA\u7840\u8BBE\u65BD\u6570\u636E\u4FE1\u606F\uFF0C\u80FD\u5927\u5927\u8282\u7701\u6570\u636E\u83B7\u53D6\u65F6\u95F4\uFF0C\u53EF\u4E3A\u667A\u6167\u57CE\u5E02\u5F62\u6210\u63D0\u4F9B\u66F4\u52A0\u4E30\u5BCC\u7684\u6570\u636E\u53C2\u8003\uFF0C\u5B9E\u73B0\u4E09\u7EF4\u6A21\u578B\u5E94\u7528\u6700\u4F73\u6210\u6548\u3002\u672C\u6587\u5206\u522B\u9610\u8FF0\u4E86\u5F00\u5C55\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u5DE5\u4F5C\u7684\u65B9\u6CD5\u53CA BIM \u6280\u672F\u7684\u4EF7\u503C\uFF0C\u63D0\u51FA\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u548C BIM \u6210\u679C\u878D\u5408\u6784\u5EFA\u57CE\u5E02\u4E09\u7EF4\u6A21\u578B\u7684\u65B9\u6CD5\u4E0E\u597D\u5904\u53CA\u5176\u5728\u667A\u6167\u57CE\u5E02\u4E2D\u7684\u5E94\u7528\u4EF7\u503C\u3002</p><p>\u6807\u7B7E\uFF1A\u503E\u659C\u6444\u5F71\u6D4B\u91CF\uFF1BBIM \u6280\u672F\uFF1B\u57CE\u5E02\u4E09\u7EF4\u6A21\u578B</p><p>\u57FA\u4E8E\u7269\u8054\u7F51\u6280\u672F\u98DE\u901F\u53D1\u5C55\u80CC\u666F\u4E0B\uFF0C\u57CE\u5E02\u4FE1\u606F\u5316\u3001\u6570\u5B57\u5316\u6C34\u5E73\u53D6\u5F97\u4E86\u7A81\u51FA\u6027\u6210\u5C31\uFF0C\u9010\u6E10\u5448\u73B0\u51FA\u57CE\u5E02\u667A\u6167\u5316\u6A21\u578B\uFF0C\u6211\u56FD\u9996\u6B21\u63D0\u51FA\u667A\u6167\u57CE\u5E02\u6982\u5FF5\u662F\u4E8E 2008 \u5E74\uFF0C\u968F\u540E 2013 \u5E74\u5728\u5168\u56FD\u5404\u5927\u57CE\u5E02\u5F00\u5C55\u4E86\u667A\u6167\u57CE\u5E02\u8BD5\u70B9\u5DE5\u4F5C\uFF0C\u6839\u636E\u8BD5\u70B9\u7ED3\u679C\u5145\u5206\u8BC1\u660E\u667A\u6167\u57CE\u5E02\u6784\u5EFA\u4E0D\u4F46\u6709\u5229\u4E8E\u5B9E\u73B0\u4FE1\u606F\u6570\u636E\u7684\u6709\u6548\u5171\u4EAB\uFF0C\u9084\u80FD\u8FDB\u4E00\u6B65\u63D0\u5347\u4FE1\u606F\u96C6\u6210\u5316\u6C34\u5E73\uFF0C\u800C\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u4F5C\u4E3A\u5F53\u524D\u6570\u636E\u4FE1\u606F\u83B7\u53D6\u4E3B\u8981\u624B\u6BB5\u4E4B\u4E00\uFF0C\u5177\u5907\u81EA\u52A8\u5316\u6C34\u5E73\u8F83\u9AD8\u53CA\u4FE1\u606F\u83B7\u53D6\u66F4\u52A0\u4FBF\u6377\u7B49\u4F18\u52BF\uFF0C\u80FD\u591F\u6784\u5EFA\u76F8\u5BF9\u5B8C\u5584\u7684\u57CE\u5E02\u4E09\u7EF4\u6A21\u578B\uFF0C\u800C BIM \u6280\u672F\u4F5C\u4E3A\u8FD1\u51E0\u5E74\u6700\u65B0\u4EA7\u751F\u6280\u672F\u624B\u6BB5\uFF0C\u6C47\u96C6\u4E86\u57CE\u5E02\u5355\u4E2A\u5EFA\u7B51\u7684\u5168\u751F\u547D\u5468\u671F\u4FE1\u606F\uFF0C\u7EFC\u5408\u53C2\u8003\u5168\u751F\u547D\u5468\u671F\u4E2D\u52A8\u6001\u53D8\u5316\u60C5\u51B5\u4E3A\u667A\u6167\u57CE\u5E02\u6784\u5EFA\u63D0\u4F9B\u4E86\u5207\u5B9E\u53EF\u884C\u6570\u636E\u652F\u6301\u3002\u7531\u6B64\u53EF\u4EE5\u4E86\u89E3\u5230\uFF0C\u4E3A\u5B9E\u73B0\u57CE\u5E02\u4E09\u7EF4\u6A21\u578B\u7684\u6709\u6548\u6784\u5EFA\u548C\u5B9E\u65BD\u76EE\u6807\uFF0C\u5C31\u5FC5\u987B\u5C06\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u6280\u672F\u4E0E BIM \u6280\u672F\u7ED3\u5408\u5728\u4E00\u8D77\u3002</p><p>1\u3001\u5173\u4E8E\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u548C BIM \u6280\u672F\u6982\u5FF5\u7684\u7B80\u8981\u9610\u8FF0</p><p>1.1 \u503E\u659C\u6444\u5F71\u6D4B\u91CF</p><p>\u503E\u659C\u6444\u5F71\u6280\u672F\u662F\u56FD\u9645\u6444\u5F71\u6D4B\u91CF\u9886\u57DF\u8FD1\u5341\u51E0\u5E74\u53D1\u5C55\u8D77\u6765\u7684\u4E00\u9879\u9AD8\u65B0\u6280\u672F\uFF0C\u8BE5\u6280\u672F\u901A\u8FC7\u4ECE\u4E00\u4E2A\u5782\u76F4\u3001\u56DB\u4E2A\u503E\u659C\u3001\u4E94\u4E2A\u4E0D\u540C\u7684\u89C6\u89D2\u540C\u6B65\u91C7\u96C6\u5F71\u50CF\uFF0C\u83B7\u53D6\u5230\u4E30\u5BCC\u7684\u5EFA\u7B51\u7269\u9876\u9762\u53CA\u4FA7\u89C6\u7684\u9AD8\u5206\u8FA8\u7387\u7EB9\u7406\u3002\u5B83\u4E0D\u4EC5\u80FD\u591F\u771F\u5B9E\u5730\u53CD\u6620\u5730\u7269\u60C5\u51B5\uFF0C\u9AD8\u7CBE\u5EA6\u5730\u83B7\u53D6\u7269\u65B9\u7EB9\u7406\u4FE1\u606F\uFF0C\u8FD8\u53EF\u901A\u8FC7\u5148\u8FDB\u7684\u5B9A\u4F4D\u3001\u878D\u5408\u3001\u5EFA\u6A21\u7B49\u6280\u672F\uFF0C\u751F\u6210\u771F\u5B9E\u7684\u4E09\u7EF4\u57CE\u5E02\u6A21\u578B\u3002\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u6280\u672F\u5728\u6444\u5F71\u6D4B\u91CF\u4E2D\u5360\u636E\u7740\u91CD\u8981\u5730\u4F4D\uFF0C\u5982\u8BE5\u9879\u6280\u672F\u4E0D\u4F46\u53EF\u5B9E\u73B0\u540C\u4E00\u5E73\u53F0\u591A\u53F0\u4F20\u611F\u5668\u7684\u8BBE\u7F6E\uFF0C\u8FD8\u80FD\u540C\u65F6\u83B7\u53D6\u5230\u7EB9\u7406\u5F71\u50CF\u548C\u5730\u7406\u4F4D\u7F6E\u6570\u636E\u4FE1\u606F\u3002\u5728\u501F\u52A9\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u8FDB\u884C\u4E09\u7EF4\u6A21\u578B\u6784\u5EFA\u65F6\u4E00\u5B9A\u8981\u4E8B\u5148\u5145\u5206\u638C\u63E1\u503E\u659C\u6444\u5F71\u57FA\u7840\u7406\u8BBA\u77E5\u8BC6\uFF0C\u5E76\u4F9D\u636E\u6B64\u5C55\u5F00\u5177\u4F53\u5DE5\u4F5C\u7684\u8BE6\u7EC6\u63A2\u8BA8\u3002\u540C\u65F6\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u671F\u95F4\u5177\u4F53\u6D89\u53CA\u5230\u8BA1\u7B97\u673A\u7CFB\u7EDF\u3001\u9AD8\u7CBE\u51C6\u6D4B\u91CF\u88C5\u7F6E\u3001\u76F8\u673A\u53CA GPS \u63A5\u6536\u673A\u7B49\u6784\u4EF6\uFF0C\u5176\u4E2D\u76F8\u673A\u5FC5\u987B\u4E25\u683C\u6309\u7167\u89C4\u5B9A\u987A\u5E8F\u8FDB\u884C\u6392\u5217\uFF0C\u4EE5\u4FBF\u4E8E\u5E2E\u52A9\u5DE5\u4F5C\u4EBA\u5458\u5168\u9762\u83B7\u53D6\u5230\u5404\u4E2A\u89C6\u89D2\u5F71\u50CF\u8D44\u6599\uFF0C\u5E76\u4E14\u5728\u6B64\u57FA\u7840\u4E0A\u6784\u5EFA\u5B8C\u5584\u6570\u5B57\u5730\u9762\u6A21\u578B\uFF0C\u4F7F\u5176\u80FD\u591F\u5145\u5206\u53D1\u6325\u51FA\u5730\u7406\u7A7A\u95F4\u53C2\u8003\u4EF7\u503C\u3002</p><p>\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u5DE5\u4F5C\u6D41\u7A0B</p><p>1.2BIM \u6280\u672F</p><p>\u5EFA\u7B51\u4FE1\u606F\u6A21\u578B\uFF08Building Information Modeling\uFF09\u662F\u4EE5\u5EFA\u7B51\u5DE5\u7A0B\u9879\u76EE\u7684\u5404\u9879\u76F8\u5173\u4FE1\u606F\u6570\u636E\u4F5C\u4E3A\u57FA\u7840\uFF0C\u901A\u8FC7\u6570\u5B57\u4FE1\u606F\u4EFF\u771F\u6A21\u62DF\u5EFA\u7B51\u7269\u6240\u5177\u6709\u7684\u771F\u5B9E\u4FE1\u606F\uFF0C\u901A\u8FC7\u4E09\u7EF4\u5EFA\u7B51\u6A21\u578B\uFF0C\u5B9E\u73B0\u5DE5\u7A0B\u8BBE\u8BA1\u3001\u65BD\u5DE5\u3001\u76D1\u7406\u3001\u7269\u4E1A\u7BA1\u7406\u3001\u8BBE\u5907\u7BA1\u7406\u3001\u6570\u5B57\u5316\u52A0\u5DE5\u7B49\u529F\u80FD\u3002\u5B83\u5177\u6709\u4FE1\u606F\u5B8C\u5907\u6027\u3001\u4FE1\u606F\u5173\u8054\u6027\u3001\u4FE1\u606F\u4E00\u81F4\u6027\u3001\u53EF\u89C6\u5316\u3001\u534F\u8C03\u6027\u3001\u6A21\u62DF\u6027\u3001\u4F18\u5316\u6027\u548C\u53EF\u51FA\u56FE\u6027\u516B\u5927\u7279\u70B9\u3002\u901A\u5E38\u6765\u8BF4\uFF0C\u57FA\u4E8E\u4E0D\u540C\u89D2\u5EA6\u5BF9 BIM \u6280\u672F\u6982\u5FF5\u7406\u89E3\u4E5F\u662F\u5B58\u5728\u7740\u8F83\u5927\u5DEE\u8DDD\uFF0C\u5176\u4E2D\u8F6F\u4EF6\u751F\u4EA7\u5382\u5BB6\u8BA4\u4E3A BIM \u5C31\u662F\u4E00\u79CD\u65B0\u578B\u8F6F\u4EF6\uFF0C\u786C\u4EF6\u751F\u4EA7\u5382\u5BB6\u5219\u8BA4\u4E3A BIM \u5C5E\u4E8E\u5DE5\u4F5C\u6027\u80FD\u8F83\u5F3A\u5DE5\u4F5C\u7AD9\uFF0C\u800C\u9879\u76EE\u8BBE\u8BA1\u5355\u4F4D\u8BA4\u4E3A BIM \u4E3B\u8981\u5305\u542B\u4E09\u7EF4\u7684\u7BA1\u7EBF\u7EFC\u5408\u3001\u78B0\u649E\u68C0\u6D4B\u7B49\u73AF\u8282\u5DE5\u4F5C\uFF1B\u627F\u5305\u5546\u8BA4\u4E3A BIM \u5305\u542B\u73B0\u573A\u76D1\u7BA1\u548C 4D\u30015D \u6A21\u62DF\u7B49\u529F\u80FD\u3002\u5176\u5B9E BIM \u6280\u672F\u5C31\u662F\u6307\u5229\u7528\u4E09\u7EF4\u6570\u5B57\u6A21\u578B\u4E3A\u8F7D\u4F53\uFF0C\u5DF2\u6570\u636E\u4E3A\u6838\u5FC3\uFF0C\u9488\u5BF9\u9879\u76EE\u5F00\u5C55\u57CE\u5E02\u89C4\u5212\u8BBE\u8BA1\u3001\u65BD\u5DE5\u3001\u8FD0\u7EF4\u7B49\u4E00\u7CFB\u5217\u5DE5\u4F5C\uFF0C\u5728\u9879\u76EE\u6574\u4E2A\u751F\u547D\u5468\u671F\u4E2D\u626E\u6F14\u7740\u81F3\u5173\u91CD\u8981\u89D2\u8272\u3002BIM \u6280\u672F\u8FD8\u5305\u542B\u4EE5\u4E0B\u51E0\u70B9\u4EF7\u503C\uFF1A\u7B2C\u4E00\uFF0C\u53EF\u4EE5\u660E\u786E\u5C55\u73B0\u9879\u76EE\u6210\u679C\uFF0C\u5927\u5927\u964D\u4F4E\u5B9E\u65BD\u671F\u95F4\u5931\u8BEF\u51FA\u73B0\u53EF\u80FD\u6027\uFF0C\u907F\u514D\u51FA\u73B0\u91CD\u590D\u52B3\u52A8\u64CD\u4F5C\u60C5\u51B5\uFF0C\u5E76\u4E14\u8FD8\u80FD\u6709\u6548\u7F29\u77ED\u9879\u76EE\u6D41\u7A0B\uFF0C\u5728\u8282\u7701\u6295\u8D44\u6210\u672C\u57FA\u7840\u4E0A\u83B7\u5F97\u7ECF\u6D4E\u5229\u6DA6\u6700\u5927\u5316\u3002\u7B2C\u4E8C\uFF0C\u6709\u5229\u4E8E\u4FDD\u8BC1\u89C4\u8303\u6587\u4EF6\u7684\u5B8C\u5584\u6027\uFF0C\u4FC3\u4F7F\u8BBE\u8BA1\u65B9\u3001\u65BD\u5DE5\u65B9\u5747\u80FD\u66F4\u597D\u53C2\u4E0E\u5230\u5B9E\u65BD\u8FC7\u7A0B\u4E2D\u53BB\uFF0C\u5207\u5B9E\u63D0\u9AD8\u9884\u7B97\u7EFC\u5408\u80FD\u529B\uFF0C\u4FC3\u4F7F\u9879\u76EE\u80FD\u591F\u987A\u5229\u5F00\u5C55\u3002\u7B2C\u4E09\uFF0C\u6709\u6548\u7F13\u89E3\u9879\u76EE\u5B9E\u65BD\u671F\u95F4\u5B58\u5728\u77DB\u76FE\u51B2\u7A81\uFF0C\u771F\u6B63\u7406\u89E3\u8BBE\u8BA1\u65B9\u5B9E\u9645\u8BBE\u8BA1\u610F\u56FE\uFF0C\u6700\u5927\u9650\u5EA6\u63D0\u9AD8\u9879\u76EE\u8D28\u91CF\u6C34\u5E73\u3002</p><p>\u5EFA\u7B51\u4FE1\u606F\u6A21\u578B</p><p>2\u3001\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u548C BIM \u6280\u672F\u5728\u4E09\u7EF4\u6A21\u578B\u6784\u5EFA\u4E2D\u7684\u5E94\u7528\u65B9\u6CD5</p><p>2.1 \u503E\u659C\u6444\u5F71\u6D4B\u91CF\u5728\u4E09\u7EF4\u6A21\u578B\u6784\u5EFA\u4E2D\u7684\u5E94\u7528</p><p>\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u624B\u6BB5\u5728\u4E09\u7EF4\u6A21\u578B\u6784\u5EFA\u4E2D\u7684\u5177\u4F53\u5E94\u7528\u4E3B\u8981\u5305\u542B\u4EE5\u4E0B\u6B65\u9AA4\uFF1A\u7B2C\u4E00\uFF0C\u591A\u89C6\u5F71\u50CF\u9884\u5904\u7406\u3002\u901A\u5E38\u5728\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u5DE5\u4F5C\u5177\u4F53\u5F00\u5C55\u65F6\uFF0C\u591A\u89C6\u5F71\u50CF\u9884\u5904\u7406\u5DE5\u4F5C\u53EF\u4EE5\u8BF4\u5360\u636E\u7740\u91CD\u8981\u5730\u4F4D\uFF0C\u5373\u6307\u5F71\u50CF\u7684\u51E0\u4F55\u9884\u5904\u7406\uFF0C\u5F7B\u5E95\u6D88\u9664\u51E0\u4F55\u53EF\u80FD\u5B58\u5728\u7684\u5931\u8BEF\u73B0\u8C61\uFF0C\u5982\u4F20\u611F\u5668\u5185\u90E8\u53D8\u5F62\u5931\u8BEF\u6216\u4F20\u611F\u5668\u5916\u90E8\u53D8\u5F62\u5931\u8BEF\uFF0C\u5176\u4E2D\u5185\u90E8\u5931\u8BEF\u4EA7\u751F\u6839\u672C\u539F\u56E0\u5C31\u5728\u4E8E\u4F20\u611F\u5668\u81EA\u8EAB\u6027\u80FD\u3001\u6570\u503C\u6307\u6807\u4E25\u91CD\u8D85\u51FA\u6807\u51C6\u72B6\u6001\uFF1B\u800C\u5916\u90E8\u5931\u8BEF\u4EA7\u751F\u539F\u56E0\u5219\u5728\u4E8E\u53D7\u5230\u5927\u6C14\u73AF\u5883\u3001\u5730\u52BF\u5730\u8C8C\u53CA\u4F20\u611F\u5668\u4F4D\u7F6E\u53D8\u5316\u7B49\u5F71\u54CD\u3002\u7B2C\u4E8C\uFF0C\u5300\u5149\u3002\u5F80\u5F80\u5728\u5F71\u50CF\u8D44\u6599\u83B7\u53D6\u671F\u95F4\uFF0C\u53D7\u5230\u5FEB\u95E8\u901F\u5EA6\u3001\u5149\u7167\u53CA\u5929\u6C14\u7B49\u56E0\u7D20\u5F71\u54CD\uFF0C\u5F71\u50CF\u5FC5\u5B9A\u4F1A\u51FA\u73B0\u5206\u5E03\u5DEE\u5F02\u8F83\u5927\u73B0\u8C61\uFF0C\u8FDB\u800C\u4E0D\u5229\u4E8E\u4E09\u7EF4\u6A21\u578B\u7684\u6709\u6548\u6784\u5EFA\u5E94\u7528\uFF0C\u5E76\u4E14\u5728\u6B64\u671F\u95F4\u6700\u5E38\u89C1\u5300\u5149\u5300\u8272\u8BA1\u7B97\u65B9\u6CD5\u6709\u9891\u7387\u57DF\u5300\u5149\u3001\u81EA\u9002\u5E94\u6A21\u677F\u5300\u5149\u53CA Wallis \u5300\u5149\u7B49\uFF0C\u5176\u4E2D Wallis \u5300\u5149\u53EF\u4EE5\u8BF4\u662F\u5E94\u7528\u6548\u679C\u6700\u4F73\u65B9\u6CD5\u4E4B\u4E00\u3002\u7B2C\u4E09\uFF0C\u7EB9\u7406\u6620\u5C04\u3002\u6240\u8C13\u7EB9\u7406\u6620\u5C04\u5C31\u662F\u6307\u5728\u8BA1\u7B97\u673A\u8FD0\u884C\u73AF\u5883\u4E0B\u5C06\u7EB9\u7406\u7A7A\u95F4\u4E2D\u6240\u5305\u542B\u7EB9\u7406\u50CF\u7D20\u771F\u5B9E\u53CD\u5C04\u5230\u5C4F\u5E55\u4E0A\u7684\u5168\u8FC7\u7A0B\uFF0C\u4FC3\u4F7F\u590D\u6742\u73B0\u573A\u80FD\u591F\u5448\u73B0\u51FA\u771F\u5B9E\u611F\u53D7\uFF0C\u5C5E\u4E8E\u8BA1\u7B97\u673A\u56FE\u5F62\u9886\u57DF\u4E2D\u6700\u5E38\u89C1\u6280\u672F\u624B\u6BB5\u4E4B\u4E00\u3002\u8FD9\u6837\u4E00\u6765\u867D\u7136\u4F20\u7EDF\u5149\u7167\u6A21\u578B\u53EA\u80FD\u5F62\u6210\u5355\u4E00\u5316\u666F\u7269\u8868\u9762\uFF0C\u4F46\u5982\u679C\u518D\u52A0\u4E0A\u4E30\u5BCC\u7EB9\u7406\u7EC6\u8282\u5FC5\u7136\u80FD\u5448\u73B0\u51FA\u666F\u7269\u771F\u5B9E\u611F\uFF0C\u5F7B\u5E95\u89E3\u51B3\u4EE5\u5F80\u4F20\u7EDF\u5149\u7167\u6240\u65E0\u6CD5\u8FBE\u5230\u7684\u6548\u679C\uFF0C\u5E76\u4E14\u7EB9\u7406\u6620\u5C04\u5F62\u6210\u5B9E\u8D28\u4FBF\u662F\u8981\u786E\u4FDD\u4E8C\u7EF4\u7A7A\u95F4\u70B9\u548C\u4E09\u7EF4\u7A7A\u95F4\u70B9\u5904\u4E8E\u4E00\u4E00\u5BF9\u5E94\u72B6\u6001\uFF0C\u4FBF\u4E8E\u5C06\u4E8C\u7EF4\u7A7A\u95F4\u70B9\u771F\u5B9E\u51C6\u786E\u53CD\u5C04\u5230\u4E09\u7EF4\u7ACB\u4F53\u8868\u9762\u4E0A\uFF0C\u5F97\u5230\u6700\u6EE1\u8DB3\u89C2\u770B\u8005\u89C6\u89C9\u8981\u6C42\u7684\u4E09\u7EF4\u6A21\u578B\u3002</p><p>2.2BIM \u6280\u672F\u5728\u4E09\u7EF4\u6A21\u578B\u6784\u5EFA\u4E2D\u7684\u5E94\u7528</p><p>BIM \u6280\u672F\u5E94\u7528\u59CB\u7EC8\u5F3A\u8C03\u7684\u4FBF\u662F\u52A8\u6001\u53EF\u53D8\u8FC7\u7A0B\uFF0C\u800C BIM \u6A21\u578B\u4F5C\u4E3A\u5168\u751F\u547D\u5468\u671F\u4FE1\u606F\u6570\u636E\u7684\u4E3B\u8981\u8868\u8FBE\u8F7D\u4F53\uFF0C\u5BF9\u51E0\u4F55\u4E09\u7EF4\u6A21\u578B\u6784\u5EFA\u6709\u7740\u6781\u5927\u5E2E\u52A9\u4F5C\u7528\uFF0C\u53EF\u4EE5\u8BF4\u4ECE\u6700\u5F00\u59CB\u7684\u9879\u76EE\u8BBE\u8BA1\u9636\u6BB5\uFF0CBIM \u6A21\u578B\u5C31\u5DF2\u7136\u627F\u8F7D\u4E86\u4FE1\u606F\uFF0C\u968F\u7740\u9879\u76EE\u5B9E\u65BD\u7684\u4E0D\u65AD\u63A8\u8FDB\uFF0CBIM \u6A21\u578B\u627F\u8F7D\u4FE1\u606F\u4E5F\u5C06\u5F97\u5230\u66F4\u52A0\u4E30\u5BCC\u62D3\u5C55\uFF0C\u5C06\u5176\u5408\u7406\u5E94\u7528\u5230\u5DE5\u7A0B\u7684\u5404\u9636\u6BB5\u4E4B\u4E2D\uFF0C\u5145\u5206\u4F53\u73B0\u51FA BIM \u6A21\u578B\u5728\u5168\u751F\u547D\u5468\u671F\u4E2D\u5B58\u5728\u4EF7\u503C\u3002\u540C\u65F6\u4E09\u7EF4\u6570\u5B57\u6280\u672F\u4F5C\u4E3A BIM \u6280\u672F\u4E2D\u91CD\u8981\u7EC4\u6210\u5185\u5BB9\u4E4B\u4E00\uFF0C\u8D2F\u7A7F\u4E8E\u6574\u4E2A\u9879\u76EE\u8FC7\u7A0B\uFF0C\u4E0D\u4EC5\u6DB5\u76D6\u4F17\u591A\u51E0\u4F55\u3001\u7269\u7406\u53CA\u6027\u80FD\u4FE1\u606F\uFF0C\u8FD8\u80FD\u5B9A\u671F\u66F4\u65B0 BIM \u6A21\u578B\u52A8\u6001\u6570\u636E\uFF0C\u5927\u5927\u63D0\u9AD8\u5DE5\u4F5C\u6548\u7387\u3002\u9664\u6B64\u4E4B\u5916\uFF0C\u6570\u636E\u50A8\u5B58\u548C\u8BBF\u95EE\u6280\u672F\u4E5F\u4E3A BIM \u6A21\u578B\u6784\u5EFA\u63D0\u4F9B\u5207\u5B9E\u53EF\u884C\u53C2\u8003\u4F9D\u636E\uFF0C\u5177\u4F53\u5C06\u5176\u5212\u5206\u4E3A\u4EE5\u4E0B\u4E24\u70B9\uFF1A\u7B2C\u4E00\uFF0C\u7ED3\u6784\u5316\u6570\u636E\u3002\u4E3B\u8981\u662F\u6307\u516C\u5F00\u6570\u636E\u6807\u51C6\u6761\u4EF6\u4E0B\u4EA7\u751F\u7684\u6570\u636E\u4FE1\u606F\uFF0C\u53EF\u5C06\u5176\u50A8\u5B58\u5230\u6570\u636E\u5E93\u4E4B\u4E2D\uFF0C\u5C5E\u4E8E BIM \u6838\u5FC3\u6570\u636E\uFF1B\u7B2C\u4E8C\uFF0C\u800C\u975E\u7ED3\u6784\u5316\u6570\u636E\u5219\u662F\u6307 BIM \u6A21\u578B\u57FA\u4E8E\u4E0D\u540C\u8868\u8FBE\u5F62\u5F0F\u5206\u4E3A\u4E13\u4E1A\u8F6F\u4EF6\u6A21\u578B\u548C\u6807\u51C6\u6A21\u578B\u4E24\u79CD\uFF0C\u5176\u4E2D\u6807\u51C6\u6A21\u578B\u5BF9 BIM \u6570\u636E\u5171\u4EAB\u6709\u7740\u66F4\u597D\u5E2E\u52A9\u6548\u679C\uFF0C\u800C\u4E13\u4E1A\u8F6F\u4EF6\u6A21\u578B\u5219\u4E3B\u8981\u7528\u4E8E\u51C6\u786E\u771F\u5B9E\u8868\u8FBE\u9879\u76EE\u6570\u636E\u4FE1\u606F\uFF0C\u4FC3\u4F7F\u4E09\u7EF4\u6A21\u578B\u6784\u5EFA\u5E94\u7528\u8FBE\u5230\u5B8C\u7F8E\u72B6\u6001\u3002\u5728\u6B64\u671F\u95F4\u8FD8\u9700\u6CE8\u610F\u4E00\u70B9\u5C31\u662F\u4E3A\u5145\u5206\u4FDD\u8BC1\u4E09\u7EF4\u6A21\u578B\u6784\u5EFA\u6570\u636E\u7684\u5B8C\u5584\u6027\uFF0C\u8FD8\u9700\u79EF\u6781\u6784\u5EFA\u4FE1\u606F\u96C6\u6210\u5E73\u53F0\uFF0C\u9488\u5BF9\u8BBE\u8BA1\u73AF\u8282\u3001\u65BD\u5DE5\u73AF\u8282\u53CA\u8FD0\u8425\u73AF\u8282\u6D89\u53CA\u6570\u636E\u4FE1\u606F\u5C55\u5F00\u4E0D\u65AD\u66F4\u65B0\u4E30\u5BCC\uFF0C\u4ECE\u800C\u4FBF\u4E8E\u5B9E\u73B0\u4FE1\u606F\u7684\u826F\u597D\u52A8\u6001\u5316\u7BA1\u7406\u5E94\u7528\u3002</p><p>2.3 \u503E\u659C\u6444\u5F71\u6D4B\u91CF\u4E0E BIM \u6280\u672F\u7ED3\u5408\u5E94\u7528\u6784\u5EFA\u57CE\u5E02\u4E09\u7EF4\u6A21\u578B</p><p>\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u6280\u672F\u80FD\u5728\u77ED\u65F6\u95F4\u5185\u6784\u5EFA\u57CE\u5E02\u5927\u8303\u56F4\u65E2\u6709\u5730\u7269\u5730\u8C8C\u7684\u6A21\u578B\uFF0C\u5373\u83B7\u53D6\u4E09\u7EF4\u6570\u5B57\u5730\u9762\u6A21\u578B\uFF0C\u7136\u800C\uFF0C\u5176\u83B7\u53D6\u7684\u57CE\u5E02\u4E09\u7EF4\u6A21\u578B\u4E0D\u542B\u5BA4\u5185\u6A21\u578B\u548C\u5730\u4E0B\u5EFA\u3001\u6784\u7B51\u7269\u7684\u6A21\u578B\uFF1BBIM \u6280\u672F\u6070\u6070\u53EF\u4F5C\u4E3A\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u6280\u672F\u83B7\u53D6\u4E0D\u5230\u7684\u53CA\u6A21\u578B\u90E8\u5206\u7684\u8865\u5145\uFF0C\u5E76\u4E14 BIM \u6280\u672F\u4F5C\u4E3A\u4E00\u9879\u5DE5\u7A0B\u5168\u751F\u547D\u5468\u671F\u7684\u6280\u672F\uFF0C\u4E0D\u4EC5\u4EC5\u662F\u6784\u5EFA\u4E86\u5B8C\u5584\u7684\u4E09\u7EF4\u6A21\u578B\uFF0C\u91CD\u8981\u7684\u662F\u6A21\u578B\u643A\u5E26\u7684\u6570\u636E\uFF0C\u662F\u4E30\u5BCC\u7684\uFF0C\u662F\u53EF\u5EF6\u7EED\u7684\u3002BIM \u6570\u636E\u548C\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u6570\u636E\u7ED3\u5408\u5BF9\u4E8E\u6784\u5EFA\u667A\u6167\u57CE\u5E02\u7684\u91CD\u8981\u7EC4\u6210\u90E8\u5206\u2014\u57CE\u5E02\u4E09\u7EF4\u7A7A\u95F4\u4FE1\u606F\u662F\u975E\u5E38\u6709\u6548\u7684\u6570\u636E\u83B7\u53D6\u65B9\u6CD5\u3002\u901A\u8FC7\u5728\u57CE\u5E02\u4E09\u7EF4\u5730\u7406\u4FE1\u606F\u5E73\u53F0\u91CC\u6574\u5408 BIM \u6570\u636E\u53CA\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u6570\u636E\uFF0C\u53EF\u4EE5\u6784\u5EFA\u4E00\u4E2A\u66F4\u52A0\u771F\u5B9E\u7684\u57CE\u5E02\u4E09\u7EF4\u4FE1\u606F\u6A21\u578B\u3002</p><p>\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u6210\u679C\u4E0E BIM \u7ED3\u5408</p><p>\u7ED3\u675F\u8BED\uFF1A</p><p>\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u4F5C\u4E3A\u5F53\u524D\u53D1\u5C55\u901F\u5EA6\u6700\u5FEB\u7684\u9AD8\u79D1\u6280\u4E4B\u4E00\uFF0C\u80FD\u9AD8\u6548\u3001\u65B9\u4FBF\u5730\u83B7\u53D6\u5230\u57CE\u5E02\u5730\u9762\u4E30\u5BCC\u7684\u6570\u636E\u4FE1\u606F\u8D44\u6E90\uFF0C\u5E76\u6784\u5EFA\u57CE\u5E02\u4E09\u7EF4\u6A21\u578B\u3002BIM \u4F5C\u4E3A\u4E00\u9879\u5DE5\u7A0B\u5168\u751F\u547D\u5468\u671F\u5E94\u7528\u7684\u6280\u672F\uFF0C\u4E3B\u8981\u662F\u4EE5\u4E09\u7EF4\u6A21\u578B\u4E3A\u8F7D\u4F53\uFF0C\u5305\u542B\u7740\u4F17\u591A\u6570\u636E\u4FE1\u606F\uFF0C\u53EF\u5177\u4F53\u5E94\u7528\u5230\u5DE5\u7A0B\u8BBE\u8BA1\u3001\u65BD\u5DE5\u3001\u8FD0\u7EF4\u7BA1\u7406\u4E2D\u3002\u800C\u4EE5\u4E0A\u4E24\u8005\u7684\u7ED3\u5408\u7ED9\u6784\u5EFA\u667A\u6167\u57CE\u5E02\u4E2D\u7684\u57CE\u5E02\u57FA\u7840\u7A7A\u95F4\u4FE1\u606F\u5E26\u6765\u4E86\u975E\u5E38\u91CD\u5927\u7684\u6280\u672F\u652F\u6491\u3002</p><p>\u53C2\u8003\u6587\u732E\uFF1A</p><p>[1]\u5218\u5C1A\u851A\uFF0C\u674E\u8D85\u5B97\uFF0C\u9B4F\u7FA4\u7B49.\u73B0\u4EE3\u822A\u6D4B\u65B0\u65B9\u6CD5\u4E0E BIM \u6280\u672F\u5EFA\u6A21\u7684\u62D3\u5C55\u5E94\u7528[J].\u4E2D\u56FD\u6C34\u8FD0\uFF08\u4E0B\u534A\u6708\uFF09\uFF0C2016\uFF0C16\uFF0812\uFF09\uFF1A63-65\uFF0C68.</p><p>[2]\u8D75\u8339.\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u6280\u672F\u5728\u57CE\u5E02\u8F68\u9053\u4EA4\u901A\u5DE5\u7A0B\u4E2D\u7684\u5E94\u7528[J].\u57CE\u5E02\u5EFA\u7B51\uFF0C2017\uFF082\uFF09\uFF1A131\uFF0C160.</p><p>[3]\u9B4F\u65B0.\u6444\u5F71\u6D4B\u91CF\u4E0E BIM \u5EFA\u6A21\u5728\u5C71\u533A\u6C34\u5229\u9879\u76EE\u52D8\u6D4B\u8BBE\u8BA1\u548C\u65BD\u5DE5\u4E2D\u7684\u5E94\u7528[J].\u5EFA\u7B51\u5DE5\u7A0B\u6280\u672F\u4E0E\u8BBE\u8BA1\uFF0C2017\uFF0828\uFF09\uFF1A781-781.</p>',25),a=[e];function t(i,n){return B(),I("div",null,a)}var r=p(_,[["render",t],["__file","\u503E\u659C\u6444\u5F71\u6D4B\u91CF\u4E0EBIM\u6280\u672F\u5B9E\u73B0\u57CE\u5E02\u4E09\u7EF4\u6A21\u578B\u7684\u6784\u5EFA\u4E0E\u5E94\u7528.html.vue"]]);export{r as default};
